// init layout fix
function initLayoutFix(){
	var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	if (isTouchDevice) {
		var win = jQuery(window);
		var hold = jQuery('.win-height');

		function handleHeight(){
			hold.css({maxHeight: ''});
			setTimeout(function(){
				hold.css({maxHeight: hold.outerHeight()});
			}, 200);
		}

		handleHeight();
		win.on('orientationchange', handleHeight);
	}
}

// init tab links
function initTabLinks(){
	var body = jQuery('body');
	jQuery('.tab-link').on('click', function(e){
		e.preventDefault();
		var navInstance = body.data('MobileNav');
		var animSpeed = jQuery(window).scrollTop() > 0 ? 650 : 0;
		jQuery('html, body').stop().animate({scrollTop: 0}, animSpeed, function(){
			if (navInstance && !navInstance.isOpened()) {
				navInstance.show();
			}
		});
		jQuery('.nav-tabs a[href="'+ this.getAttribute('href') +'"]').tab('show');
	});
}

// init textarea autosize
function initTextarea(){
	jQuery('.contact-form textarea').textareaAutoSize();
}

// init promo
function initPromo(){
	ResponsiveHelper.addRange({
		'..767': {
			on: function() {
				jQuery('.history-block').fadeGallery({
					slides: 'div.step',
					circularRotation: false,
					autoHeight: false,
					useSwipe: false,
					animSpeed: 650,
				});
			},
			off: function() {
				if (jQuery('.history-block').data('FadeGallery')) {
					jQuery('.history-block').data('FadeGallery').destroy();
				}
			}
		},
		'768..': {
			on: function() {
				jQuery('.history-block').promo({
					box: '.container',
					slides: '[data-num]'
				});
			}
		}
	});

	jQuery('.intro-holder').promo({
		box: '.intro-box',
		slides: '[data-num]',
		onInit: function(self){
			ResponsiveHelper.addRange({
				'768..': {
					on: function() {
						var maxHeight = 0;
						self.innerMask = self.holder.find('.inner-gallery .scroll-mask')
						self.innerSlides = self.innerMask.find('.scroll-slide');
						self.handleHeight = function(){
							maxHeight = 0;
							self.innerSlides.each(function(){
								if (jQuery(this).outerHeight() > maxHeight) {
									maxHeight = jQuery(this).outerHeight();
								}
							})
							self.innerMask.css({height: maxHeight});
						}
						self.handleHeight();
						self.bindHandlers(['handleHeight']);
						self.win.on('resize orientationchange', self.handleHeight);
						setTimeout(function(){
							self.handleHeight();
						}, 1000);
					},
					off: function() {
						self.win.off('resize orientationchange', self.handleHeight);
						setTimeout(function(){
							self.innerMask.removeAttr('style');
						}, 0);
					}
				}
			});
		},
		onDestroy: function(self){
			self.win.off('resize orientationchange', self.handleHeight);
			setTimeout(function(){
				self.innerMask.removeAttr('style');
			}, 0);
		}
	});
}

