// init datepicker
function initDatepicker(){
	var timer, visible;
	var datepicker = jQuery('.date-picker');

	datepicker.datepicker({
		firstDay: 1,
		minDate: 0,
		showOtherMonths: true,
		selectOtherMonths: true,
		dayNamesMin: ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'],
		beforeShow: function(){
			visible = true;
		},
		onClose: function(){
			visible = false;
		}
	});

	jQuery(window).on('resize orientationchange', function(){
		if (visible) {
			datepicker.datepicker('hide');
			clearTimeout(timer);
			timer = setTimeout(function(){
				datepicker.datepicker('show');
			}, 500);
		}
	});
}

// init timepicker
function initTimePicker(){
	jQuery('.time-picker').clockpicker({
		default: 'now',
		vibrate: false,
		twelvehour: true,
		align: 'right'
	});
}

// init file upload
function initFileUpload(){
	var maxFiles = 3;
	var curFiles = 0;
	var maxFileSize = 25 * 1024 * 1024; // 25 MB

	jQuery('.file-upload').each(function(){
		var hold = jQuery(this);
		var input = hold.find('input:file');
		var fileList = hold.find('.file-list');
		var fileItem = fileList.find('>li').clone();

		fileList.empty();

		input.fileupload({
			url: '/uploads1',
			dropZone: '.file-upload',
			progress: function(e, data){
				var progress = parseInt(data.loaded / data.total * 100, 10);
				data.context.find('.bar').css({width: progress + '%'});
				if (progress === 100) {
					data.context.removeClass('uploading');
				}
			},
			add: function(e, data){
				if (curFiles >= maxFiles) {
					alert('Maximum number of files exceeded.');
				}
				if (data.files[0].size > maxFileSize) {
					alert('File "'+data.files[0].name+'" is too large. Max size ~25MB.');
				}
				if (curFiles < maxFiles && data.files[0].size < maxFileSize) {
					var item = fileItem.clone();
					var $itemFile = $('<input/>').attr({
						type: 'hidden',
						name: 'attachments[]'
					}).appendTo(item);

					curFiles++;

					data.context = item.appendTo(fileList);
					item.find('.name').html(data.files[0].name);

					// cancel loading item
					item.find('.cancel').on('click', function(e){
						e.preventDefault();
						item.fadeOut(function(){
							item.remove();
							curFiles--;
						});
					});

					jqXHR = data.submit().success(function(response, textStatus, jqXHR){
						if (textStatus === 'success') {
							item.removeClass('uploading');
							$itemFile.val(response.path);
							// remove loaded item
							item.find('.remove').on('click', function(e){
								e.preventDefault();
								jQuery.ajax({
									url: response.deleteUrl,
									method: response.deleteType
								}).success(function(){
									item.fadeOut(function(){
										item.remove();
										curFiles--;
									});
								});
							});
						}
					});
				}
			}
		});

		jQuery(document).bind('drop dragover', function(e){
			e.preventDefault();
		});
	})
}

